void lcdShowBmp(uint8_t *bmp){
  for(uint8_t j = 0; j < 240; j++){
	  for(uint8_t i = 0; i < 50; i++){
		  lcdBuffer[j][i] = ~bmp[50*j+i];
	  }
  }
}



