// Set screen orientation
// input:
//   orientation - one of LCD_ORIENT_xx values
void SMLCD_Orientation(uint8_t orientation) {
	if (orientation & (LCD_ORIENT_CW | LCD_ORIENT_CCW)) {
		scr_width  = SCR_H;
		scr_height = SCR_W;
	} else {
		scr_width  = SCR_W;
		scr_height = SCR_H;
	}
	lcd_orientation = orientation;
}

